import styled from 'styled-components'
import CallMadeIcon from '@material-ui/icons/CallMade';

const Landing = () => {
  return (
    <Container>
        <h1>Love Quiz</h1>
        <img className="top-img" src="https://media.licdn.com/dms/image/C5603AQGnU9XmdSJr8w/profile-displayphoto-shrink_400_400/0/1543696323109?e=2147483647&v=beta&t=PHgDSVAmC-UcUVE3TjjroyjjAVc2aG6uv-jIpX-yV28" alt="" />
        <h2>By Dr. Jenry Auther </h2>
        {/* <p>Powered by insights from 100,000+ Indian profiles, our AI predicts your ideal partner by understanding your unique preferences and ensuring a perfect match.</p> */}
        <h3>"10 quizzes designed to help you better understand your love life and guide you in finding your perfect match."</h3>
        <a href="/quiz" className="btn">
            Start Quiz <CallMadeIcon/>
        </a>
    </Container>
  )
}

export default Landing

const Container = styled.div`
    background-color: whitesmoke;
    min-height: 100vh;
    width: 100%;
    max-width: 500px;
    padding: 50px;
    padding-top: 0;

