    align-items: center;
    justify-content: center;

    h1{
        font-weight: 500;
        text-transform: uppercase;
        font-size: 3rem;
        letter-spacing: 0.5rem;
        text-align: center;
        color: #ffffff;
        /* font-family: "Londrina Sketch", sans-serif; */
    }

    h2{
        font-weight: 600;
        font-size: 1.25rem;
        text-align: center;
        color: #ffffff; 
    }

    h3{
        margin-top: 20px;
        font-weight: 600;
        font-size: 1.5rem;
        text-align: center;
        color: #ffffff; 
    }

    p{
        text-align: center;
        font-weight: 300;
        font-style: italic;
        margin-top: 20px;
    }

    .top-img{
        width: 200px;
        aspect-ratio: 1/1;
