import React, { useState, useEffect } from "react";
import styled from 'styled-components';
import { collection, addDoc } from "firebase/firestore";
import { db } from '../firebase';

const quizData = [
    {
        question: "1. What is the capital of India?",
        options: ["Delhi", "Mumbai", "Kolkata", "Chennai"]
    },
    {
        question: "What is the largest planet in our solar system?",
        options: ["Earth", "Mars", "Jupiter", "Saturn"]
    },
    {
        question: "Who wrote 'Hamlet'?",
        options: ["Shakespeare", "Hemingway", "Tolstoy", "Orwell"]
    },
    {
        question: "What is the speed of light?",
        options: ["300,000 km/s", "150,000 km/s", "1,000,000 km/s", "75,000 km/s"]
    },
    {
        question: "Which is the smallest country in the world?",
        options: ["Vatican City", "Monaco", "Nauru", "Malta"]
    },
    {
        question: "What is the boiling point of water?",
        options: ["100°C", "50°C", "0°C", "200°C"]
    },
    {
        question: "Which planet is known as the Red Planet?",
        options: ["Mars", "Venus", "Earth", "Jupiter"]
    },
    {
        question: "Who painted the Mona Lisa?",
        options: ["Da Vinci", "Picasso", "Van Gogh", "Rembrandt"]
    },
    {
        question: "What is the tallest mountain in the world?",
        options: ["Mount Everest", "K2", "Kangchenjunga", "Lhotse"]
    },
    {
        question: "Which element has the chemical symbol 'O'?",
        options: ["Oxygen", "Hydrogen", "Carbon", "Nitrogen"]
    }
];

const Quiz = () => {
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [selectedOption, setSelectedOption] = useState(null);
    const [answers, setAnswers] = useState({});
    const [submitting, setSubmitting] = useState(false);
    const [showResults, setShowResults] = useState(false);

    // Load saved answers and question index from localStorage on component mount
    useEffect(() => {
        const savedAnswers = localStorage.getItem("quizAnswers");
        const savedIndex = localStorage.getItem("currentQuestionIndex");

        if (savedAnswers) {
            const parsedAnswers = JSON.parse(savedAnswers);
            setAnswers(parsedAnswers);
            setSelectedOption(parsedAnswers[savedIndex] || null);
        }

        if (savedIndex) {
            setCurrentQuestionIndex(Number(savedIndex));
        }
    }, []);

    const handleOptionChange = (option) => {
        setSelectedOption(option);
    };

    const handleNextQuestion = async () => {
        const updatedAnswers = { ...answers, [currentQuestionIndex]: selectedOption };
        setAnswers(updatedAnswers);
        localStorage.setItem("quizAnswers", JSON.stringify(updatedAnswers));
        localStorage.setItem("currentQuestionIndex", currentQuestionIndex + 1);

        if (currentQuestionIndex < quizData.length - 1) {
            setCurrentQuestionIndex(currentQuestionIndex + 1);
            setSelectedOption(updatedAnswers[currentQuestionIndex + 1] || null);
        } else {
            // Last question: Submitting quiz
            setSubmitting(true);
            try {
                await addDoc(collection(db, "quizResults"), {
                    answers: updatedAnswers,
                    timestamp: new Date(),
                });
                console.log("Quiz results submitted to Firebase.");
                localStorage.removeItem("quizAnswers");
                localStorage.removeItem("currentQuestionIndex");
                alert("Quiz submitted successfully!");
            } catch (error) {
                console.error("Error submitting quiz results: ", error);
            } finally {
                setSubmitting(false);
            }
        }
    };

    const currentQuestion = quizData[currentQuestionIndex];
    const isLastQuestion = currentQuestionIndex === quizData.length - 1;

    return (
        <Container>
            <div className="question">
                <div className="question-text">{currentQuestion.question}</div>
                <div className="options">
                    {currentQuestion.options.map((option, index) => (
                        <div key={index} className="option">
                            <label>
                                <input
                                    type="radio"
                                    name={`question-${currentQuestionIndex}`}
                                    value={option}
                                    checked={selectedOption === option}
                                    onChange={() => handleOptionChange(option)}
                                />
                                {option}
                            </label>
                        </div>
                    ))}
                </div>
            </div>
            <button
                className="btn1"
                onClick={handleNextQuestion}
                disabled={!selectedOption || submitting}
            >
                {isLastQuestion ? (submitting ? "Submitting..." : "Submit Quiz") : "Save and Next"}
            </button>
        </Container>
    );
};


export default Quiz

const Container = styled.div`
    background-color: whitesmoke;
    height: 100vh;
    width: 100%;
    max-width: 500px;
    padding: 20px;

    background: url('https://sereneplus.in/wp-content/uploads/2024/04/cute-wallpaper.jpg') no-repeat center center;
    background-size: cover;

    .question{
        .question-text{
            color: white;
            font-size: 1.75rem;
            font-weight: 500;
            margin-bottom: 10px;
        }

        .options{
            .option{
                color: white;
                font-size: 1.25rem;

                label{
                    color: white;
                    font-size: 1.25rem;
                }

                input{
                    margin-right: 10px;
                }
            }
        }
    }

    .btn1{
        margin-top: 30px;
        padding: 5px 20px;
        border-radius: 100px;
        background-color: #ff6161;
        border: none;
        color: white;
        font-size: 1.15rem;
        text-decoration: none;
    }
`
