import React, { useState, useEffect } from "react";
import styled from 'styled-components'

const Landing = () => {
  return (
    <Container>
        <h1>Love Quiz</h1>
        {/* <img className="top-img" src="https://i.pinimg.com/originals/8c/bb/9b/8cbb9b70ce1e8f18f721119a064c1c3b.gif" alt="" /> */}
        <p>Powered by insights from 100,000+ Indian profiles, our AI predicts your ideal partner by understanding your unique preferences and ensuring a perfect match.</p>
        <a href="/quiz" className="btn">
            Get started
        </a>
    </Container>
  )
}

export default Landing

const Container = styled.div`
    background-color: whitesmoke;
    height: 100vh;
    width: 100%;
    max-width: 500px;
    padding: 50px;
    padding-top: 0;

    background: url('https://e1.pxfuel.com/desktop-wallpaper/360/407/desktop-wallpaper-cleijhanne-dona-on-design-kawaii-panda-thumbnail.jpg') no-repeat center center;
    background-size: cover;

    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;

    h1{
        font-weight: 800;
        font-size: 4rem;
        text-align: center;
        color: orange;
        font-family: "Londrina Sketch", sans-serif;
    }

    p{
        text-align: center;
        font-weight: 300;
        font-style: italic;
        margin-top: 20px;
    }

    .top-img{
        width: 200px;
        aspect-ratio: 1/1;
        border-radius: 50%;
        margin: 20px 0;
    }

    .btn{
        margin-top: 30px;
        padding: 15px 30px;
        border-radius: 100px;
        background-color: orange;
        border: none;
        color: white;
        font-size: 1rem;
        text-decoration: none;
    }
`
