import React, { useState, useEffect, useRef } from "react";
import styled from 'styled-components'
import CallMadeIcon from '@material-ui/icons/CallMade';
import FiberManualRecordIcon from '@material-ui/icons/FiberManualRecord';
import AttachFileIcon from '@material-ui/icons/AttachFile';

const Landing = () => {
    return (
    <Container>
        <div className="main-content">
            <div className="logo-x-dp">
                <img src="https://media.licdn.com/dms/image/v2/D5603AQHc7m2ahjLvsQ/profile-displayphoto-shrink_800_800/B56ZSi9EoCHEAc-/0/1737900706914?e=1743638400&v=beta&t=ts0xpBGygsHZo2VS_B2v3vASU6izmlJ01yu3_wIQZv4" alt="" />
            </div>
            <div className="name">Atanu Nayak</div>
            <div className="about-header">Final Year computer science student @Jadavpur University</div>
            <div className="about-desc">Hello, I'm Atanu Nayak, a software developer from India. I had the opportunity to intern with Samsung R&D during the summer of 2024.</div>

            <div className="follow-btn">Follow</div>
        </div>
        <div className="join-us"></div>
    </Container>
  )
}

export default Landing

const Container = styled.div`
    width: 100vw;
    min-height: 100vh;
    background-color: #333;

    padding: 30px;

    display: flex;
    align-items: center;
    /* justify-content: center; */
    flex-direction: column;

    .main-content{
        width: calc(100vw - 60px);
        max-width: 450px;
        background-color: #3e3e3e;
        min-height: calc(100vh - 150px);
        border-radius: 30px;

        display: flex;
        flex-direction: column;
        align-items: center;

        padding: 35px;

        .logo-x-dp{
            height: 120px;
            aspect-ratio: 1/1;
            overflow: hidden;
            border-radius: 50%;

            img{
                width: 100%;
            }
        }

        .name{
            margin-top: 20px;
            font-weight: 500;
            text-align: center;
        }

        .about-header{
            margin-top: 10px;
            font-weight: 500;
            font-size: 0.85rem;
            text-align: center;
        }

        .about-desc{
            margin-top: 5px;
            font-weight: 200;
            font-size: 0.85rem;
            text-align: center;
        }

        .follow-btn{
            font-size: 0.85rem;
            margin-top: 30px;
            padding: 10px;
        }
    }

    .join-us{
        width: calc(100vw - 60px);
        max-width: 450px;
        background-color: whitesmoke;
        border-radius: 30px;
        height: 60px;
        margin-top: 30px;
    }
`
