import React, { useState, useEffect } from "react";
import styled from 'styled-components';
import { collection, addDoc } from "firebase/firestore";
import { db } from '../firebase';

const quizData = [
    {
        question: "1. Are you more of a dog person or a cat person?",
        options: ["Dog person", "Cat person", "Both", "Neither"]
    },
    {
        question: "2. What kind of person do you think you are at heart?",
        options: ["Creative", "Practical", "Pure-hearted", "Logical"]
    },
    {
        question: "3. In relationships, do you tend to be more expressive or reserved?",
        options: ["Very expressive", "Somewhat expressive", "More reserved", "Completely private"]
    },
    {
        question: "4. When faced with a challenge, how do you respond?",
        options: ["Dive right in", "Carefully plan first", "Ask for help", "Take time to process"]
    },
    {
        question: "5. What is your approach to handling conflicts?",
        options: ["Communicate openly", "Avoid confrontation", "Compromise", "Stand your ground"]
    },
    {
        question: "6. What do you value most in a partner?",
        options: ["Trust", "Loyalty", "Fun", "Emotional connection"]
    },
    {
        question: "7. Do you prefer deep conversations or light-hearted banter?",
        options: ["Deep conversations", "A mix of both", "Light-hearted banter", "Depends on the mood"]
    },
    {
        question: "8. What is your love language?",
        options: ["Words of affirmation", "Acts of service", "Physical touch", "Quality time"]
    },
    {
        question: "9. How do you show affection?",
        options: ["Through words", "Through actions", "Through gifts", "Through spending time together"]
    },
    {
        question: "10. Where would you prefer to go on a date?",
        options: ["Restaurants for eating together", "Zoo for long walks", "Gardens for peaceful moments", "Party locations for fun and excitement"]
    }
];

const Quiz = () => {
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
    const [selectedOption, setSelectedOption] = useState(null);
    const [answers, setAnswers] = useState({});
    const [submitting, setSubmitting] = useState(false);
    const [showResults, setShowResults] = useState(false);

    useEffect(() => {
        const savedAnswers = localStorage.getItem("quizAnswers");
        const savedIndex = localStorage.getItem("currentQuestionIndex");

        if (savedAnswers) {
            const parsedAnswers = JSON.parse(savedAnswers);
            setAnswers(parsedAnswers);
            setSelectedOption(parsedAnswers[savedIndex] || null);
        }

        if (savedIndex) {
            setCurrentQuestionIndex(Number(savedIndex));
        }
    }, []);

    const handleOptionChange = (option) => {
        setSelectedOption(option);
    };

    const handleNextQuestion = async () => {
        const updatedAnswers = { ...answers, [currentQuestionIndex]: selectedOption };
        setAnswers(updatedAnswers);
        localStorage.setItem("quizAnswers", JSON.stringify(updatedAnswers));
        localStorage.setItem("currentQuestionIndex", currentQuestionIndex + 1);

        if (currentQuestionIndex < quizData.length - 1) {
            setCurrentQuestionIndex(currentQuestionIndex + 1);
            setSelectedOption(updatedAnswers[currentQuestionIndex + 1] || null);
        } else {
            // Last question: Submitting quiz
            setSubmitting(true);
            try {
                await addDoc(collection(db, "quizResults"), {
                    answers: updatedAnswers,
                    timestamp: new Date(),
                });
                console.log("Quiz results submitted to Firebase.");
                localStorage.removeItem("quizAnswers");
                localStorage.removeItem("currentQuestionIndex");
                alert("Quiz submitted successfully!");

                setShowResults(true);
            } catch (error) {
                console.error("Error submitting quiz results: ", error);
            } finally {
                setSubmitting(false);
            }
        }
    };

    const currentQuestion = quizData[currentQuestionIndex];
    const isLastQuestion = currentQuestionIndex === quizData.length - 1;

    return (
        <GreatContainer>
            {
                showResults ? <Container2>
                    <div className="result-text">
                        We've compared your quiz results with over 1 million participants, and congratulationsâ€”your perfect match is <a href="https://www.instagram.com/nayakpenguin/">@NayakPenguin</a> on Instagram!
                    </div>
                    <img src="https://media.tenor.com/AL4ZMzBLtvgAAAAj/aww-cute.gif" alt="" />
                </Container2> : (
                    <Container>
                        <div className="question">
                            <div className="question-text">{currentQuestion.question}</div>
                            <div className="options">
                                {currentQuestion.options.map((option, index) => (
                                    <div key={index} className="option">
                                        <label>
                                            <input
                                                type="radio"
                                                name={`question-${currentQuestionIndex}`}
                                                value={option}
                                                checked={selectedOption === option}
                                                onChange={() => handleOptionChange(option)}
                                            />
                                            {option}
                                        </label>
                                    </div>
                                ))}
                            </div>
                        </div>
                        <button
                            className="btn1"
                            onClick={handleNextQuestion}
                            disabled={!selectedOption || submitting}
                        >
                            {isLastQuestion ? (submitting ? "Submitting..." : "Submit Quiz") : "Save and Next"}
                        </button>
                    </Container>
                )
            }
        </GreatContainer>
    );
};


export default Quiz

const GreatContainer = styled.div`
    background-color: whitesmoke;
    height: 100vh;
    width: 100%;
    max-width: 500px;
`

const Container = styled.div`
    background-color: whitesmoke;
    height: 100vh;
    width: 100%;
    max-width: 500px;
    padding: 20px;

    background: url('https://sereneplus.in/wp-content/uploads/2024/04/cute-wallpaper.jpg') no-repeat center center;
    background-size: cover;

    .question{
        .question-text{
            color: white;
            font-size: 1.75rem;
            font-weight: 500;
            margin-bottom: 10px;
        }

        .options{
            .option{
                color: white;
                font-size: 1.25rem;

                label{
                    color: white;
                    font-size: 1.25rem;
                }

                input{
                    margin-right: 10px;
                }
            }
        }
    }

    .btn1{
        margin-top: 30px;
        padding: 5px 20px;
        border-radius: 100px;
        background-color: #ff6161;
        border: none;
        color: white;
        font-size: 1.15rem;
        text-decoration: none;
    }
`

const Container2 = styled.div`
    background-color: #ca5154;
    height: 100vh;
    width: 100%;
    max-width: 500px;
    padding: 20px;   
    padding-top: 50px;
    display: flex;
    align-items: center;
    flex-direction: column;

    .result-text{
        color: white;
        font-size: 1.75rem;
        font-weight: 500;
        margin-bottom: 10px;
        text-align: center;

        a{
            font-weight: 500;
            color: #99beff;
            display: block;
        }
    }

    img{
        width: 60%;
        margin-left: -7.5%;
    }
`
