import React, { useState, useEffect } from "react";
import styled from 'styled-components'
import CallMadeIcon from '@material-ui/icons/CallMade';
import img from '../riya.png';
import FiberManualRecordIcon from '@material-ui/icons/FiberManualRecord';
import AttachFileIcon from '@material-ui/icons/AttachFile';

const Landing = () => {
  return (
    <Container>
        <h2>Exactly four months ago, I visited the Pattern Drive office for the first time, purely out of fun, and it turned out to be one of the best decisions of my life.</h2>
        <div className="image">
            <div className="top-holder"><AttachFileIcon/></div>
            <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSx-2oOuGwA0-czHP_E4aKsFQCBJrF6eeFFFA&s" alt="" />
        </div>
    </Container>
  )
}

export default Landing

const Container = styled.div`
    background-color: #222121;
    min-height: 100vh;
    width: 100%;
    max-width: 500px;
    padding: 50px;

    // background: url('https://w0.peakpx.com/wallpaper/189/297/HD-wallpaper-cute-butterfly-aesthetic.jpg') no-repeat center center;
    background-size: cover;

    display: flex;
    flex-direction: column;
    align-items: center;

    h2{
        font-weight: 600;
        font-size: 1.15rem;
        text-align: center;
        color: #ffffff; 
    }

    .image{
        position: relative;
        margin-top: 30px;
        width: 90%;
        /* aspect-ratio: 1/1.25; */
        background-color: white;

        padding: 10px 10px 40px 10px;

        .top-holder{
            position: absolute;
            /* width: 100%; */

            svg{
                font-size: 2rem;
            }

            text-align: center;
            margin-bottom: 5px;
        }

        img{
            width: 100%;
        }
    }
`
